mapper.insert:
INSERT INTO transit_page (
    user_id, title, content, wait_time,
    background_color, text_color, button_color,
    powered_by_name, powered_by_icon, status
) VALUES (
    #{userId}, #{title}, #{content}, #{waitTime},
    #{backgroundColor}, #{textColor}, #{buttonColor},
    #{poweredByName}, #{poweredByIcon}, #{status}
)

mapper.update:
UPDATE transit_page
SET status = #{status}
WHERE user_id = #{userId}
