mapper.select:
SELECT * FROM url_mapping WHERE status = 1
AND (short_code LIKE CONCAT('%', #{keyword}, '%') OR original_url LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY create_time DESC LIMIT #{offset}, #{limit}

SELECT * FROM url_mapping
WHERE status = 1 AND user_id = #{userId}

    AND (short_code LIKE CONCAT('%', #{keyword}, '%') 
    OR original_url LIKE CONCAT('%', #{keyword}, '%'))

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}
